operations.body:
query GetProduct($productId: ID!) {
  product(id: $productId) {
    id
    title
    description
    totalInventory
    priceRangeV2 {
      maxVariantPrice {
        amount
        currencyCode
        __typename
      }
      __typename
    }
    __typename
  }
}

query PaginateThroughProducts($numberOfProductVariantsToTake: Int = 10) {
  products(first: $numberOfProductVariantsToTake) {
    edges {
      product: node {
        id
        title
        variants(first: $numberOfProductVariantsToTake) {
          nodes {
            title
            price
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

query getWeatherData($search: String!) {
  getWeatherData(search: $search) {
    conditions
    forecastSnowFall
    temperature
    windSpeed
    currentSnowDepth
    __typename
  }
}
